import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

export default function ChatbotSection() {
  const [messages, setMessages] = useState([
    { sender: 'agent', text: 'Hi! What can I help you with?' }
  ]);
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    const userMsg = { sender: 'user', text: input };
    setMessages(msgs => [...msgs, userMsg]);
    setInput('');
    try {
      const sessionId = localStorage.getItem('sessionId') || 'test-session';
      const res = await axios.post('http://localhost:5000/api/chat', {
        message: input,
        history: messages,
        sessionId,
      });
      setMessages(msgs => [...msgs, { sender: 'agent', text: res.data.reply }]);
    } catch (err) {
      setMessages(msgs => [...msgs, { sender: 'agent', text: 'Sorry, there was an error connecting to the assistant.' }]);
    }
  };


  const handleClear = () => {
    setMessages([{ sender: 'agent', text: 'Hi! What can I help you with?' }]);
    setInput('');
  };

  return (
    <section className="chatbot-section">
      <div className="chatbot-header">
        <div>Chatbot</div>
        <button className="btn-clear" onClick={handleClear}>Clear Chat</button>
      </div>
      <div className="chatbot-messages">
        {messages.map((msg, idx) => (
          <div key={idx} className={`chat-message ${msg.sender}`}>{msg.text}</div>
        ))}
        <div ref={chatEndRef} />
      </div>
      <form className="chatbot-input" onSubmit={handleSend}>
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
    </section>
  );
}
