import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import ChatBubble from './components/ChatBubble';
import VoiceButton from './components/VoiceButton';
import LoadingSpinner from './components/LoadingSpinner';

const API_URL = 'http://localhost:5000/api/chat';

// Generate or retrieve a persistent sessionId
function getSessionId() {
  let id = localStorage.getItem('sessionId');
  if (!id) {
    id = crypto.randomUUID ? crypto.randomUUID() : Math.random().toString(36).substr(2, 16);
    localStorage.setItem('sessionId', id);
  }
  return id;
}

const INITIAL_MSG = { sender: 'agent', text: 'Hi! What can I help you with?', timestamp: new Date().toLocaleString() };

export default function App() {
  const [messages, setMessages] = useState([INITIAL_MSG]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;
    const userMsg = { sender: 'user', text: input, timestamp: new Date().toLocaleString() };
    setMessages((msgs) => [...msgs, userMsg]);
    setInput('');
    setLoading(true);
    try {
      const sessionId = getSessionId();
      const res = await axios.post(API_URL, { message: input, history: messages, sessionId });
      setMessages((msgs) => [...msgs, { sender: 'agent', text: res.data.reply, timestamp: new Date().toLocaleString() }]);
    } catch (err) {
      setMessages((msgs) => [...msgs, { sender: 'agent', text: 'Sorry, there was an error connecting to the assistant.', timestamp: new Date().toLocaleString() }]);
    }
    setLoading(false);
  };

  const handleClear = () => {
    setMessages([ { ...INITIAL_MSG, timestamp: new Date().toLocaleString() } ]);
    setInput('');
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh', background: '#fafbfc' }}>
      <div style={{ width: 600, marginTop: 40, background: '#fff', borderRadius: 12, boxShadow: '0 2px 16px #0001', display: 'flex', flexDirection: 'column', minHeight: 600 }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '16px 24px', borderBottom: '1px solid #eee' }}>
          <span style={{ fontWeight: 600, fontSize: 22 }}>Paul B. Z Bot</span>
          <button onClick={handleClear} title="Clear chat" style={{ background: '#f3f4f6', border: 'none', borderRadius: 8, padding: '6px 12px', fontWeight: 500, color: '#007aff', cursor: 'pointer', fontSize: 14 }}>Clear</button>
        </div>
        <div style={{ flex: 1, overflowY: 'auto', padding: 24, display: 'flex', flexDirection: 'column', gap: 16 }}>
          {messages.map((msg, idx) => <ChatBubble key={idx} message={msg} />)}
          {loading && <LoadingSpinner />}
          <div ref={chatEndRef} />
        </div>
        <form onSubmit={handleSend} style={{ display: 'flex', alignItems: 'center', borderTop: '1px solid #eee', padding: 16, gap: 8 }}>
          <input
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Message..."
            style={{ flex: 1, fontSize: 16, padding: '8px 12px', borderRadius: 8, border: '1px solid #ddd', outline: 'none' }}
            autoFocus
            disabled={loading}
          />
          <button type="submit" disabled={loading} style={{ background: '#111', color: '#fff', border: 'none', borderRadius: '50%', width: 36, height: 36, cursor: loading ? 'not-allowed' : 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: loading ? 0.6 : 1 }}>
            <span style={{ fontSize: 18 }}>&#9658;</span>
          </button>
          <VoiceButton disabled={loading} onResult={setInput} />
        </form>
      </div>
    </div>
  );
}
