import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Products({ sku = '302120' }) {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);
    axios.get(`http://localhost:5000/api/product/${sku}`)
      .then(res => {
        setProduct(res.data.product);
        setLoading(false);
      })
      .catch(() => {
        setProduct(null);
        setError('Product not found.');
        setLoading(false);
      });
  }, [sku]);

  if (loading) return <div>Loading product...</div>;
  if (error || !product) return <div>No product preview available.</div>;

  return (
    <section className="products-section" style={{ maxHeight: '520px', overflowY: 'auto' }}>
      <h2>Product Preview</h2>
      <div className="product-card" key={product.id}>
        <div className="product-image-placeholder" />
        <div className="product-details">
          <div className="product-name">{product.name}</div>
          <div className="product-desc">{product.description}</div>
          <div className="product-price">${product.price}</div>
          <div className="product-sku">SKU: {product.SKU}</div>
          {product.URL && <a href={product.URL} target="_blank" rel="noopener noreferrer">Product Link</a>}
        </div>
      </div>
    </section>
  );
}
