import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Products() {
  const [sku, setSku] = useState('302120');
  const [inputSku, setInputSku] = useState('302120');
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!sku) return;
    setLoading(true);
    setError(null);
    axios.get(`http://localhost:5000/api/product/${sku}`)
      .then(res => {
        setProduct(res.data.product);
        setLoading(false);
      })
      .catch(() => {
        setProduct(null);
        setError('Product not found.');
        setLoading(false);
      });
  }, [sku]);

  return (
    <section className="products-section" style={{ maxHeight: '520px', overflowY: 'auto' }}>
      <h2>Product Preview</h2>
      <form
        onSubmit={e => {
          e.preventDefault();
          setSku(inputSku.trim());
        }}
        style={{ marginBottom: 16 }}
      >
        <input
          value={inputSku}
          onChange={e => setInputSku(e.target.value)}
          placeholder="Enter SKU"
          style={{ padding: 6, fontSize: 16, width: 180, marginRight: 8 }}
        />
        <button type="submit">Preview</button>
      </form>
      {loading ? (
        <div>Loading product...</div>
      ) : error || !product ? (
        <div>No product preview available.</div>
      ) : (
        <div className="product-card" key={product.id}>
          <div className="product-image-placeholder" />
          <div className="product-details">
            <div className="product-name">{product.name}</div>
            <div className="product-desc">{product.description}</div>
            <div className="product-price">${product.price}</div>
            <div className="product-sku">SKU: {product.SKU}</div>
            {product.URL && <a href={product.URL} target="_blank" rel="noopener noreferrer">Product Link</a>}
          </div>
        </div>
      )}
    </section>
  );
}
