import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import ChatBubble from './components/ChatBubble';
import VoiceButton from './components/VoiceButton';

const API_URL = 'http://localhost:5000/api/chat';

export default function App() {
  const [messages, setMessages] = useState([
    { sender: 'agent', text: 'Hi! What can I help you with?', timestamp: new Date().toLocaleString() },
  ]);
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    const userMsg = { sender: 'user', text: input, timestamp: new Date().toLocaleString() };
    setMessages((msgs) => [...msgs, userMsg]);
    setInput('');
    try {
      const res = await axios.post(API_URL, { message: input, history: messages });
      setMessages((msgs) => [...msgs, { sender: 'agent', text: res.data.reply, timestamp: new Date().toLocaleString() }]);
    } catch (err) {
      setMessages((msgs) => [...msgs, { sender: 'agent', text: 'Sorry, there was an error connecting to the assistant.', timestamp: new Date().toLocaleString() }]);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh', background: '#fafbfc' }}>
      <div style={{ width: 400, marginTop: 40, background: '#fff', borderRadius: 12, boxShadow: '0 2px 16px #0001', display: 'flex', flexDirection: 'column', minHeight: 600 }}>
        <div style={{ padding: '16px 24px', borderBottom: '1px solid #eee', fontWeight: 600, fontSize: 22 }}>Playground<span style={{ color: '#999', fontSize: 16, marginLeft: 8 }}>â“˜</span></div>
        <div style={{ flex: 1, overflowY: 'auto', padding: 24, display: 'flex', flexDirection: 'column', gap: 16 }}>
          {messages.map((msg, idx) => <ChatBubble key={idx} message={msg} />)}
          <div ref={chatEndRef} />
        </div>
        <form onSubmit={handleSend} style={{ display: 'flex', alignItems: 'center', borderTop: '1px solid #eee', padding: 16, gap: 8 }}>
          <input
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Message..."
            style={{ flex: 1, fontSize: 16, padding: '8px 12px', borderRadius: 8, border: '1px solid #ddd', outline: 'none' }}
            autoFocus
          />
          <button type="submit" style={{ background: '#111', color: '#fff', border: 'none', borderRadius: '50%', width: 36, height: 36, cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <span style={{ fontSize: 18 }}>&#9658;</span>
          </button>
          <VoiceButton disabled />
        </form>
      </div>
    </div>
  );
}
