import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

import LoadingSpinner from './LoadingSpinner';
import VoiceButton from './VoiceButton';
import ChatBubble from './ChatBubble';

export default function ChatbotSection() {
  const [messages, setMessages] = useState([
    {
      sender: 'agent',
      text: `Yes, we carry a couple of expandable 100-foot garden hoses:

1. XHose Pro 5/8 In. Dia. X 100 Ft. L. Expandable Hose: This hose is made of tough Dac-5 fiber webbing with a drag-resistant polyester outer layer and solid brass fittings. It's lightweight, crush-resistant, kink-free, and automatically expands and shrinks after use. Price: $49.99. You can find it in aisle 34 at our Lititz, PA location.
2. Hydrotech 5/8 In. x 100 Ft. Expandable Burst Proof Hose: This burst-proof hose has a thick latex lining, a heavy-duty shutoff valve, and connectors that fit most nozzles and sprinklers. Price: $29.99. (Ask an associate for location or check online.)

When using expandable garden hoses, avoid dragging them over sharp surfaces, don’t leave them pressurized when not in use, and store out of direct sun to prolong their lifespan.

Let me know if you’d like details about either item or help with alternatives!

{"type":"product_list","products":[{"name":"XHose Pro 5/8 In. Dia. X 100 Ft. L. Expandable Hose","price":49.99,"aisle":"34", "SKU": "234245"},{"name":"Hydrotech 5/8 In. x 100 Ft. Expandable Burst Proof Hose","price":29.99,"aisle":"34", "SKU": "234245"}]}`,
      timestamp: "now"
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;
    const userMsg = { sender: 'user', text: input };
    setMessages(msgs => [...msgs, userMsg]);
    setInput('');
    setLoading(true);
    try {
      let sessionId = localStorage.getItem('sessionId');
      if (!sessionId) {
        sessionId = (window.crypto?.randomUUID?.() || Date.now().toString());
        localStorage.setItem('sessionId', sessionId);
      }
      const res = await axios.post('http://localhost:5000/api/chat', {
        message: input,
        history: messages,
        sessionId,
      });

      // Check if the response looks like a tool call for openProductDetail
      let botMsg = res.data.reply;
      let productMsg = null;
      try {
        const parsed = typeof botMsg === 'string' ? JSON.parse(botMsg) : botMsg;
        if (parsed && parsed.tool === 'openProductDetail' && parsed.tool_input?.id) {
          // Fetch product detail from backend
          const prodRes = await axios.post('http://localhost:5000/api/openProductDetail', {
            sku: parsed.tool_input.id
          });
          if (prodRes.data && prodRes.data.product) {
            const p = prodRes.data.product;
            productMsg = `Product Details\nName: ${p.name || ''}\nSKU: ${p.SKU || ''}\nPrice: $${p.price || ''}\n${p.description ? 'Description: ' + p.description + '\n' : ''}${p.URL ? 'URL: ' + p.URL : ''}`;
          } else {
            productMsg = 'Product not found.';
          }
        }
      } catch (err) { /* Not a tool call, treat as normal reply */ }

      setMessages(msgs => [
        ...msgs,
        { sender: 'agent', text: productMsg || (typeof botMsg === 'string' ? botMsg : JSON.stringify(botMsg)) }
      ]);
    } catch (err) {
      setMessages(msgs => [...msgs, { sender: 'agent', text: 'Sorry, there was an error connecting to the assistant.' }]);
    }
    setLoading(false);
  };


  const handleClear = () => {
    setMessages([{ sender: 'agent', text: 'Hi! What can I help you with?' }]);
    setInput('');
    localStorage.removeItem('sessionId');
  };

  return (
    <section className="chatbot-section">
      <div className="chatbot-header">
        <div className="chatbot-title">Chatbot</div>
        <button className="btn-clear" onClick={handleClear}>Clear Chat</button>
      </div>
      <div className="chatbot-messages">
        {messages.map((msg, idx) => (
          <ChatBubble key={idx} message={msg} />
        ))}
        {loading && (
          <div style={{ display: 'flex', justifyContent: 'center', padding: 8 }}>
            <LoadingSpinner />
          </div>
        )}
        <div ref={chatEndRef} />
      </div>
      <form className="chatbot-input" onSubmit={handleSend} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type a message..."
          style={{ flex: 1 }}
        />
        <button type="submit" style={{ minWidth: 60 }}>Send</button>
        <VoiceButton disabled={loading} onResult={t => setInput(t)} />
      </form>
    </section>
  );
}
